import jsonimport requestsfrom tabulate import *def get_ticket():    requests.packages.urllib3.disable_warnings()    api_url = "https://devnetsbx-netacad-apicem-3.cisco.com/api/v1/ticket"    headers = {     "content-type": "application/json"    }    body_json = {    "password":"Xj3BDqbU",    "username":"devnetuser"    }    resp=requests.post(api_url,json.dumps(body_json),headers=headers,verify=False)    response_json = resp.json()    serviceTicket = response_json["response"]["serviceTicket"]    return serviceTicketdef print_devices():       api_url = "https://devnetsbx-netacad-apicem-3.cisco.com/api/v1/network-device"    ticket = get_ticket()    headers = {     "content-type": "application/json",     "X-Auth-Token": ticket    }    resp = requests.get(api_url, headers=headers, verify=False)    if resp.status_code != 200:     raise Exception("No se recibe estado 200. Respesta: " + resp.text)    response_json = resp.json()    device_list = []    i = 0    for item in response_json["response"]:     i+=1     device = [     i,     item["series"],     item["managementIpAddress"]     ]     device_list.append( device )    table_header = ["Number", "Series", "Management IP"]    print( tabulate(device_list, table_header) )    return table_headerdef print_hosts():    api_url = "https://devnetsbx-netacad-apicem-3.cisco.com/api/v1/host"    ticket = get_ticket()    headers = {     "content-type": "application/json",     "X-Auth-Token": ticket    }    resp = requests.get(api_url, headers=headers, verify=False)    if resp.status_code != 200:     raise Exception("No se recibe estado 200. Respesta:  " + resp.text)    response_json = resp.json()    host_list = []    i = 0    for item in response_json["response"]:     i+=1     host = [     i,     item["hostType"],     item["hostIp"]     ]     host_list.append( host )    table_header = ["NÃºmero", "Tipo", "IP"]    print( tabulate(host_list, table_header) )    return table_header